servers : Array
[
{
    id : X , name : "test" , ip : "@ip" , status : "Active/Down"
}
]

*/
<template>
  <div class="holders hoder-supervision" >
    <div class="Welcomeback colorfull-gradient-1">
        <h1>Welcome back, {{AuthStore.username}}!ðŸ‘‹</h1>
        <p>How things are going today?</p>
    </div>
    <queryNavbar :theme="'white'" >
      <template #Title>
        Servers
      </template>
    </queryNavbar>
    <tableExtension>
      <template #tableRows >
            <tr  v-for="server in servers" :key="server.id" >
                <td class="server-name">{{ server.name }}</td>
                <td>
                    <p>{{ server.ip }}</p>
                </td>
                <td>
                    <p class="holder-status">
                        <span :class="{ 'status-dot-active' : server.status == 'Active','status-dot-down' : server.status == 'Down'  }"  class="status-dot" ></span>
                        <span>{{ server.status }}</span>
                    </p>
                </td>
            </tr>
      </template>
    </tableExtension>
  </div>
</template>
<script>
import { UseAuthStore } from "@/store/store.js"
import queryNavbar from "@/components/interraction/query/query-navbar.vue"
import tableExtension from "@/components/interraction/tables/table-withextension.vue"
export default{
    name : 'supervision-view',
    data(){
        return{
          AuthStore : UseAuthStore(),
          servers : []
        } 
    },
    components : {
      queryNavbar,
      tableExtension
    },
    methods : {
      getServers(){
            this.servers = [
                {
                    id : 5,
                    name : "Onboarding01-uat",
                    ip : "192.168.1.104",
                    status : 'Active'
                },
                {
                    id : 15,
                    name : "Onboarding02-uat",
                    ip : "192.168.1.107",
                    status : 'Down'
                },
            ]
        }
    },
    beforeMount(){
        this.getServers();
    }
}
</script>
<style scoped>

.hoder-supervision{
  height: 100%;
  width: 100%;

  display: grid;
  grid-template-rows: 120px  50px  1fr;
}
.Welcomeback{
  height: auto;

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: start;

  gap : 5px;
  padding : var(--medieum-padding);
  box-sizing: border-box;
  border-radius: var(--medieum-border-radius);



  color:white;
}

</style>
