<template>
  <div class="holders hoder-supervision">
    <div class="Welcomeback colorfull-gradient-1">
      <h1>Welcome back, {{AuthStore.username}}!ðŸ‘‹</h1>
      <p>How things are going today?</p>
    </div>
    <queryNavbar @input-changed="onInputChanged" :theme="'white'">
      <template #Title>
        Servers {{ " "+ (this.$route.query.UAT ? "UAT" : "Prod" ) }}
      </template>
    </queryNavbar>
    <tableExtension>
      <template #tableHeaders>
        <td>Name</td>
        <td>IP</td>
        <td>Status</td>
      </template>
      <template #tableRows>
        <tr v-for="server in servers" :key="server.id" @click="displayContainersOf(server)">
          <td class="server-name">{{ server.name }}</td>
          <td>
            <p>{{ server.ip }}</p>
          </td>
          <td>
            <p class="holder-status">
              <span
                :class="{ 'status-dot-active' : server.status == 'Active','status-dot-down' : server.status == 'Down'  }"
                class="status-dot"></span>
              <span>{{ server.status }}</span>
            </p>
          </td>
        </tr>
      </template>
    </tableExtension>

    <popupDisplay v-if="displayContainersOnPopUp"
      @close-popup="displayContainersOnPopUp = false; containersPopUpDisplay = []">
      <slot>
        <div class="popup-holder">
          <tableExtension v-if="containersPopUpDisplay.length != 0">
            <template #tableHeaders>
              <td>Name</td>
              <td>ID</td>
              <td>PORTS</td>
              <td>STATUS</td>
            </template>
            <template #tableRows>
              <tr v-for="container in containersPopUpDisplay" :key="container.id">
                <td class="server-name">{{ container.name }}</td>
                <td>
                  <p>{{ container.id }}</p>
                </td>
                <td>
                  <p>{{ container.ports }}</p>
                </td>
                <td>
                  <p>{{ container.status }}</p>
                </td>
              </tr>
            </template>
          </tableExtension>
          <p v-else >This server has no runing container...</p>
        </div>
      </slot>
    </popupDisplay>
  </div>
</template>
<script>
import { UseAuthStore } from "@/store/store.js"
import apiFetch from "@/common/api.service.js"

import popupDisplay from "@/components/interraction/popups/popup-display.vue"
import queryNavbar from "@/components/interraction/query/query-navbar.vue"
import tableExtension from "@/components/interraction/tables/table-withextension.vue"
export default{
    name : 'supervision-view',
    data(){
        return{
          AuthStore : UseAuthStore(),
          servers : [],
          displayContainersOnPopUp : false,
          containersPopUpDisplay : []
        } 
    },
    components : {
      queryNavbar,
      tableExtension,
      popupDisplay
    },
    methods : {
      getServers(){
        let endpoint = "/" + this.$route.UAT ? 'UAT' : 'Prod' +'/servers';
        apiFetch.get(endpoint,{"username" :  this.username , "password" : this.password},false)
        .then( (response)=>{
          console.log(response);
          this.AuthStore.setUsername = this.username;
          this.AuthStore.setConnexionType = {
              UAT : this.ServerType == 'Pord' ? false : true,
              PROD : this.ServerType == 'Pord' ? true : false,
          };
          var query ={};
           if(this.ServerType == 'UAT'){
            query = {
              UAT : true
            }
           }else{
            query = {
              Prod : false
            }
           }
          this.$router.push({name : 'supervision' , query : { ...query }});
        })
        .catch( (error)=>{
          console.log(error)
          this.LogingIn = false;
          this.messageErreur = error.msg;
        });

      },
      onInputChanged(inputValue){
        this.getServersWithNameLike(inputValue)
      },
      getServersWithNameLike(NameLike){
        console.log(NameLike)
        //fetch here after
      },
      displayContainersOf(server){
        //fetch for containers here 
        console.log("fetching...",server)
        //then assigne to containersPopUpDisplay
        this.displayContainersOnPopUp = true;
        this.containersPopUpDisplay = server.containers;
      },
      addHashToLocation(params) {
        history.pushState(
          {},
          null,
          this.$route.path + '#' + encodeURIComponent(params)
        )
      }
    },
    beforeMount(){


        this.getServers();
    },
    beforeCreate(){

    }
}
</script>
