/* -------------------------------------------------------------------------- */
/*                                  JSON DATA                                 */
/* -------------------------------------------------------------------------- */
/*  

servers : Array
[
{
    id : X , name : "test" , ip : "@ip" , status : "Active/Down"
}
]

*/

<template>
    <div class="table">
        <table>
            <tr class="table-header" >
                <td>Name</td>
                <td>IP</td>
                <td>Status</td>
            </tr>
            <tr  v-for="server in servers" :key="server.id" >
                <td class="server-name">{{ server.name }}</td>
                <td>
                    <p>{{ server.ip }}</p>
                </td>
                <td>
                    <p class="holder-status">
                        <span :class="{ 'status-dot-active' : server.status == 'Active','status-dot-down' : server.status == 'Down'  }"  class="status-dot" ></span>
                        <span>{{ server.status }}</span>
                    </p>
                </td>
            </tr>



        </table>
    </div>
</template>
<script>
export default{
    name : "table-extension",
    data(){
        return{
            servers : [
                {
                    id : 5,
                    name : "Onboarding01-uat",
                    ip : "192.168.1.104",
                    status : 'Active'
                },
                {
                    id : 15,
                    name : "Onboarding02-uat",
                    ip : "192.168.1.107",
                    status : 'Down'
                },
            ]
        }
    }
}
</script>
<style scoped>
.table table{
    width: 100%;
    background-color: var(--light-color-0);
    border-radius: var(--medieum-border-radius);

    border-collapse: collapse;

    /* overflow: hidden; */

}
.table table  tr{
    height: 50px;

    box-sizing: border-box;

    cursor: pointer;

    border-bottom: solid 1px var(--gray-color-1 );
}
.table table tr:last-child{
    border-radius: 50%;
}

.table .server-name{
    font-weight: 500;
}
.table .table-header{
    color: var(--gray-text-color-1);
    font-weight: 500;
}
.holder-status{
    display: flex;
    justify-content: flex-start;
    align-items: center;

    gap: 3px;
}
.status-dot{

    height: 8px;
    width: 8px;

    border-radius: 50%;

    background-color: gray;
    opacity: 0.65;
}
.status-dot-down{
    background-color: var(--danger-color);
}
.status-dot-active{
    background-color: var(--validation-color);
}
</style>
