import toolKit from "@/main.js"

export default class apiFetch{
    static BASE_URL = "http://localhost:5000/api";
    static get(endpoint,emit,throwErr){
        if(throwErr == null){
            throwErr = false;
        }
        return new Promise((resolve,reject)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "GET",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                }
            })
            .then((response)=>{
                if(response.status == 401){
                    toolKit.router.push('/login')
                }
                response.json()
                .then((data)=>{
                    if(emit){
                        toolKit.emitter.emit("sidebar-error",{
                            error : response.ok,
                            description : data.msg
                        })
                    }
                    if(response.ok){
                        resolve(data);
                    }else if(throwErr == true && !response.ok){
                        reject(data);
                    }
                })

            })
            .catch((err)=>{
                console.log(err);
            })
        })
    }
    static post(endpoint,payload,emit,throwError){
        return new Promise((resolve,reject)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "POST",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                },
                body : JSON.stringify({ 
                    ...payload
                  })
            })
            .then((response)=>{
                console.log(response.ok)
                response.json()
                .then((data)=>{
                    if(response.status == 401){
                        toolKit.router.push('/login')
                    }
                    if(response.ok){
                        console.log(response.ok)
                        resolve(data);
                    }else{
                        if(throwError){
                            reject(data);
                        }
                    }
                    if(emit){
                        toolKit.emitter.emit("sidebar-error",{
                            error : !response.ok,
                            description : data.msg
                        })
                    }
                })

            })
            .catch((err)=>{
                console.log(err)
            })
        })

    }
    static update(endpoint,payload,emit,throwError){
        return new Promise((resolve,reject)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "PUT",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                },
                body : JSON.stringify({ 
                    ...payload
                  })
            })
            .then((response)=>{
                console.log(response.ok)
                response.json()
                .then((data)=>{
                    if(response.status == 401){
                        toolKit.router.push('/login')
                    }
                    if(response.ok){
                        console.log(response.ok)
                        resolve(data);
                    }else{
                        if(throwError){
                            reject(data);
                        }
                    }
                    if(emit){
                        toolKit.emitter.emit("sidebar-error",{
                            error : !response.ok,
                            description : data.msg
                        })
                    }
                })

            })
            .catch((err)=>{
                console.log(err)
            })
        })

    }
    static delete(endpoint,payload,emit,throwError){
        return new Promise((resolve,reject)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "DELETE",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                },
                body : JSON.stringify({ 
                    ...payload
                  })
            })
            .then((response)=>{
                console.log(response.ok)
                response.json()
                .then((data)=>{
                    if(response.status == 401){
                        toolKit.router.push('/login')
                    }
                    if(response.ok){
                        console.log(response.ok)
                        resolve(data);
                    }else{
                        if(throwError){
                            reject(data);
                        }
                    }
                    if(emit){
                        toolKit.emitter.emit("sidebar-error",{
                            error : !response.ok,
                            description : data.msg
                        })
                    }
                })

            })
            .catch((err)=>{
                console.log(err)
            })
        })

    }
}



