import router from "@/main.js"
import emitter from "@/main.js"
export default class apiFetch{
    static BASE_URL = "http://localhost:5000/api";


    static get(endpoint,emit){
        return new Promise((resolve)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "GET",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                }
            })
            .then((response)=>{
                if(response.status == 401){
                    router.push('/login')
                }
                response.json()
                .then((data)=>{
                    if(emit){
                        this.emitter.emit("sidebar-error",{
                            error : response.ok,
                            description : data.msg
                        })
                    }
                    resolve(data);
                })

            })
        })
    }

    static post(endpoint,payload,emit){
        return new Promise((resolve,reject)=>{
            fetch(this.BASE_URL+endpoint,{
                method: "POST",
                credentials: "include", 
                headers: {
                    "Content-Type": "application/json",
                },
                body : JSON.stringify({ 
                    ...payload
                  })
            })
            .then((response)=>{
                response.json()
                .then((data)=>{
                    if(response.ok){

                        resolve(data);
                    }else{
                        reject(data);
                    }
                    if(emit){
                        this.emitter.emit("sidebar-error",{
                            error : response.ok,
                            description : data.msg
                        })
                    }
                })

            })
        })

    }
}
